import Web3 from 'web3'

export function hello(){
    alert('hello');
}

export function getPromotion(contractInstance,account){
    return new Promise(function(resolve, reject) {
        var codeArray = [];
        contractInstance.getPastEvents('Promotion', {
            filter: {sponsorAddress:account}, // Using an array means OR: e.g. 20 or 23
            fromBlock: 0,
            toBlock: 'latest'
        }, function(error, events){
          console.log(events);
          if(events.length > 0){
            // console.log(events[0].returnValues); 
            for(var i=0;i<events.length;i++){
              codeArray.push([events[i].returnValues.couponCode,events[i].returnValues.sponsorAddress,new Date(events[i].returnValues.dateCreated*1000),events[i].returnValues.targetAddress,Web3.utils.fromWei(events[i].returnValues.totalEth,"ether"),Web3.utils.fromWei(events[i].returnValues.amtPerAddress,"ether"),Web3.utils.fromWei(events[i].returnValues.minRedeemAmt,"ether"),Web3.utils.fromWei(events[i].returnValues.maxRedeemAmt,"ether"),events[i].returnValues.startDate,events[i].returnValues.endDate]);
            }
            resolve(codeArray);            
          }
          })
        .then(function(events){
        });
    });
}



