class App extends React.Component  {

  componentDidMount() {
    this.loadBlockchainData();
    this.Connect = this.Connect.bind(this);
  }

  async Connect(){
    if (window.ethereum) {
      alert('installed');
      const address = await window.ethereum.enable(); 
      alert(address);
      var outputText = 'Current Account: ' + address;
      var that = this;

      const web3 = new Web3(Web3.givenProvider);
      // Get account balance
      web3.eth.getBalance(address.toString(), function(err, result) {
      if (err) {
        console.log(err)
      } else {
        var currentBalance = (Web3.utils.fromWei(result, "ether") + " ETH");
        // console.log(currentBalance);
        outputText = outputText + " " + currentBalance;
        console.log(outputText);
        that.setState({accountInfo: outputText});
      }
    })

    console.log(outputText);
    this.setState({accountInfo: outputText});
