        <th>Min Coupon Txn Amount</th>
        <th>Max Coupon Txn Amount</th>
      </tr>
    </thead>
    <tbody>
      <tr>
      	<th><i class="fa fa-credit-card"></i></th>
        <td>Ethereum</td>
        <td>01/12/2021</td>
        <td>0x28cd15e5B23a8bA95Bb4660c3d305Fb18081c517</td>
        <td>500</td>
        <td>100</td>
        <td>15/01/2022</td>
        <td>20/01/2022</td>
        <td>2</td>
        <td>0.1</td>
        <td>0.5</td>
      </tr>
      <tr>
      	<th><i class="fa fa-credit-card"></i></th>
        <td>Doge</td>
        <td>01/12/2021</td>
        <td>0x28cd15e5B23a8bA95Bb4660c3d305Fb18081c511</td>
        <td>500</td>
        <td>100</td>
        <td>15/01/2022</td>
        <td>20/01/2022</td>
        <td>2</td>
        <td>0.1</td>
        <td>0.5</td>
      </tr>
      <tr>
      	<th><i class="fa fa-window-close-o"></i></th>
        <td>DogeElonMars</td>
        <td>01/11/2021</td>
        <td>0x28cd15e5B23a8bA95Bb4660c3d305Fb18081c510</td>
        <td>500</td>
        <td>0</td>
        <td>10/11/2021</td>
        <td>15/11/2021</td>
        <td>2</td>
        <td>0.1</td>
        <td>0.5</td>
      </tr>
    </tbody>
  </table>
  </div>
</div>

<div class="modal" id="myModal">
  <div class="modal-dialog">
    <div class="modal-content">

      <div class="modal-header">
        <h4 class="modal-title">Add new promotion</h4>
        <button type="button" class="btn-close" data-bs-dismiss="modal"></button>
      </div>

      <div class="modal-body">
        <table>
        	<tr style={{height:'45px'}}>
            	<td style={{width:'200px'}}>Promotion code:</td>
                <td><input type="text" class="form-control" name="txtPromoCode"/></td>
            </tr>
            <tr style={{height: '45px'}}>
            	<td>Ether per account:</td>
                <td><input type="text" class="form-control" name="txtEtherPerAccount"/></td>
            </tr>
            <tr style={{height: '45px'}}>
            	<td>Starting Date:</td>
                <td><input type="datetime-local" class="form-control" name="txtStartingDate"/></td>
            </tr>
            <tr style={{height:'45px'}}>
            	<td>Ending Date:</td>
                <td><input type="datetime-local" class="form-control" name="txtEndingDate"/></td>
            </tr>
            <tr style={{height: '45px'}}>
            	<td>Min Spending Amount:</td>
                <td><input type="text" class="form-control" name="txtMinSpendingAmount"/></td>
            </tr>
            <tr style={{height: '45px'}}>
            	<td>Max Spending Amount:</td>
                <td><input type="text" class="form-control" name="txtMaxSpendingAmount"/></td>
            </tr>
        </table>
      </div>

      <div class="modal-footer">
          	<button type="button" class="btn btn-success">Add New Promotion</button>
        <button type="button" class="btn btn-danger" data-bs-dismiss="modal">Close</button>
      </div>

    </div>
  </div>
</div>
</div>
  );
  }
}

export default App;
