import React, { Component } from 'react'
import logo from './logo.svg'
import './App.css'
import Web3 from 'web3'
import {ADDRESS, ABI} from './ABI'
import {hello,getPromotion} from './helper';

class Table1 extends React.Component  {

    componentDidMount() {
        this.loadBlockchainData();
      }

      async loadBlockchainData() {
        const web3 = new Web3(Web3.givenProvider);
        const chainId = await web3.eth.getChainId();
        console.log(chainId);
        this.setState({accountInfo: ''});
        var contractInstance = new web3.eth.Contract(ABI, ADDRESS);
        const accounts = await web3.eth.getAccounts();
        // await GetAllPromoCodes(contractInstance);

        var that = this;
        var codeArray = [];
        var promoOrigin = [];
        var promo = [];
        var promoDate = [];
        var PromotionLimit = [];

        promo = await getPromotion(contractInstance,accounts[0]);
        console.log(promo);
        this.setState({testData:promo});
        console.log("from Home");
        console.log(this.state.testData);

      }

    constructor(props) {
        super(props);
        this.state = {testData: ''};
        // console.log("list data is " + JSON.stringify(this.props.testData));
      }



    render(){
        const tableHeader = this.props.header;
        var listData = this.state.testData;
        return (
            <>
            <table class="table table-striped">
              <thead>
                <tr>
                {tableHeader.map((header) => <th>{header}</th>)}
                  {/* <th></th>
                  <th>Promotion Code</th>
                  <th>Date Created</th>
                  <th>Target account</th>
                  <th>Starting Ether Balance</th>
                  <th>Present Ether Balance</th>
                  <th>Starting Date</th>
                  <th>Ending Date</th>
                  <th>Ether per account</th>
                  <th>Min Coupon Txn Amount</th>
                  <th>Max Coupon Txn Amount</th> */}
                </tr>
              </thead>
              <tbody>
                {[listData].map((data) => <tr><td>{data.couponCode}</td><td>{new Date(data.dateCreated * 1000).toString()}</td><td>{data.targetAddress}</td><td>{Web3.utils.fromWei(data.totalEth,"ether").toString()}</td><td>{new Date(data.startDate*1000).toString()}</td><td>{new Date(data.endDate*1000).toString()}</td><td>{Web3.utils.fromWei(data.amtPerAddress,"ether").toString()}</td><td>{Web3.utils.fromWei(data.minRedeemAmt,"ether").toString()}</td><td>{Web3.utils.fromWei(data.maxRedeemAmt,"ether").toString()}</td></tr>)}
                {/* {listData.map((data) => <tr><td>{data.couponCode}</td><td>{data.dateCreated}</td><td>{data.targetAddress}</td><td>{data.totalEth}</td><td>{data.startDate}</td><td>{data.endDate}</td><td>{data.amtPerAddress}</td><td>{data.minRedeemAmt}</td><td>{data.maxRedeemAmt}</td></tr>)} */}
              </tbody>
            </table>
        {/* </div>
            <h1>Who lives in my garage?</h1>
            <ul>
            {listData.map((data) => data.sponsorAddress)}
            </ul> */}
            {JSON.stringify(listData)}
          </>
        );
    }
}

export default Table1;
