export function hello(){
    alert('hello');
}

export function getPromotionOrigin(contractInstance,account){
    return new Promise(function(resolve, reject) {
        var codeArray = [];
        contractInstance.getPastEvents('PromotionOrigin', {
            filter: {sponsorAddress:account}, // Using an array means OR: e.g. 20 or 23
            fromBlock: 0,
            toBlock: 'latest'
        }, function(error, events){
          console.log(events);
          if(events.length > 0){
            // console.log(events[0].returnValues); 
            for(var i=0;i<events.length;i++){
              codeArray.push(events[i].returnValues.couponCode);
            }
            resolve(codeArray);            
          }
          })
        .then(function(events){
        });
    });
}
